import React from "react";

function CreateArea() {
  const [newnote,setnewnote]= useState({
    title:"",
    content:""
  });

  function changetitle(event){
    setnewnote.title(event.target.value);
  }

  function changecontent(event){
  setnewnote.content(event.target.value);
  }


  return (
    <div>
      <form >
        <input onChange={changetitle} name="title" placeholder="Title" value={newnote.title}/>
        <textarea name="content" placeholder="Take a note..." rows="3" value={newnote.content}/>
        <button>Add</button>
      </form>
    </div>
  );
}

export default CreateArea;
